package ifelse;

public class IfElseEvenOdd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num=10;
		
		if (num%3 ==12)
		
		{
			System.out.println("Number is even number");
		}
		
		else
			
		{
			
			System.out.println("Number is odd number");
			
		}
		
		
		
		
		
	}

}
